DELIMITER ;;
/*!50003 CREATE*/ /*!50017 DEFINER=`root`@`localhost`*/ /*!50003 TRIGGER `tri_delete_chapter` BEFORE DELETE ON `chapter` FOR EACH ROW BEGIN
     DELETE FROM comment WHERE user_chapter_id IN (
         SELECT uc.id FROM user_chapter uc WHERE uc.chapter_id = OLD.id
         );
    DELETE FROM user_chapter WHERE chapter_id = OLD.id;
    UPDATE account a SET deposit = deposit+OLD.price
    WHERE a.id IN (
        SELECT u.account_id FROM user u WHERE u.id IN (
            SELECT uc.user_id FROM user_chapter uc WHERE uc.chapter_id = OLD.id)
        );
END */;;
