CREATE TABLE `comment` (
  `id` int NOT NULL AUTO_INCREMENT COMMENT '评论唯一标识',
  `user_chapter_id` int NOT NULL COMMENT '该评论所属的用户章节关系id',
  `content` char(40) NOT NULL COMMENT '评论的具体内容',
  `comment_id` int NOT NULL DEFAULT '0' COMMENT '该评论所对某一评论的id（如果是不是评论的评论则用0表示）',
  `update` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP COMMENT '评论发表时间',
  PRIMARY KEY (`id`),
  KEY `comment_fk` (`user_chapter_id`),
  CONSTRAINT `comment_fk` FOREIGN KEY (`user_chapter_id`) REFERENCES `user_chapter` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_0900_ai_ci COMMENT='评论';
